import React, { useState } from 'react'
import styled from 'styled-components'

const Card = ({ title, description, image }) => {

    const [ hover, setHover ] = useState(false)

    const Card = styled.div`
        width: clamp(250px, 20vw, 250px);
        height: clamp(250px, 20vw, 250px);
        border: 1px solid #F7EEE9;
        display: flex;
        flex-direction: column;
        overflow-Y: hidden; 
        background-Color: Black;
        text-Align: center;
        position: relative;
    `


    return (
        <Card onMouseOver={() => setHover(true)} onMouseLeave={() => setHover(false)}>
            {hover 
            ? 
            <p style={{ position:"absolute", width:"256px", height:"256px", fontColor: "white"}}>{title}</p>
            : <h1 style={{ position:"absolute", width:"100%", top: "35%", left: "50%", transform: "translate(-50%, -50%)"}}>{description}</h1>
            }
            <img alt="img" src={image} style={hover? {opacity:"75%",} : {height: "100%", width: "100%", objectFit: "fill"}}/>
        </Card> 
    )
}

export default Card
