import React from 'react'
import styled from 'styled-components'

const Box = styled.div`
  border: 10px solid white;
  min-width: 50vw;
  box-Shadow: 0 5px #666;
  max-height: 50vw;
  height: 300px;
  width: 600px;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`

const Name = styled.p`

  ${props => props.primary ? "#ffffe0" : "white"};
color: ${props => props.primary ? "white" : "#ffffe0"};

  font-size: calc(70px + 3vmin);
  margin: 0 auto;
  margin-Top: -16px; 
  margin-Bottom: -16px;
`

const NameBox = ({ word }) => {
    return (
      <Box>
        <p className="LandingPage-Name" style={{ marginTop: '10px', color: '#fffff0'}}>{word}</p>
          <Name>Kyle Gloria</Name>
          <Name></Name>
      </Box>   
    )
  }

export default NameBox
