import React, { useState } from 'react'
import styled from 'styled-components'

const Card = () => {

    const [ hover, setHover ] = useState(false)

    const Card = styled.div`
        width: clamp(250px, 20vw, 250px);
        height: clamp(250px, 20vw, 250px);
        border: 6px solid white;
        display: flex;
        flex-direction: column;
        overflow-Y: hidden; 
  `

    const CardImage = styled.img`
        &:hover {
            opactiy: 30%;
        }
    `

    return (
        <Card onMouseOver={() => setHover(true)} onMouseLeave={() => setHover(false)}>
            <div>
            {hover 
            ? 
            <p style={{ position:"absolute", width:"256px", height:"256px" }}>Hello</p>
            : <h1 style={{ position:"absolute", width:"256px", height:"256px" }}>cat</h1>
            }
            <CardImage alt="img" src="https://news.artnet.com/app/news-upload/2019/01/Cat-Photog-Feat-256x256.jpg"/>
            </div>
        </Card> 
    )
}

export default Card
