import React, { useState } from 'react'
import styled from 'styled-components'

const Card = () => {

    const [ hover, setHover ] = useState(false)

    const Card = styled.div`
    width: clamp(250px, 20vw, 250px);
    height: clamp(250px, 20vw, 250px);
    border: 6px solid white;
    display: flex;
    flex-direction: column;
    overflow-Y: hidden; 
  `

    return (
        <Card onMouseOver={() => setHover(true)} onMouseLeave={() => setHover(false)}>
            {hover 
            ? <div>
                <p style={{ position:"absolute", width:"256px", height:"256px" }}>Hello</p>
              </div>
            : <img alt="img" src="https://news.artnet.com/app/news-upload/2019/01/Cat-Photog-Feat-256x256.jpg"></img>
            }

        </Card> 
    )
}

export default Card
