import React, { useState } from 'react'
import styled from 'styled-components'

const Card = () => {

    const [ hover, setHover ] = useState(false)

    const Card = styled.div`
        width: clamp(250px, 20vw, 250px);
        height: clamp(250px, 20vw, 250px);
        max-Height: 250px;
        max-Width: 250px;
        border: 1px solid silver;
        display: flex;
        flex-direction: column;
        overflow-Y: hidden; 
        background-Color: Black;
    `


    return (
        <Card onMouseOver={() => setHover(true)} onMouseLeave={() => setHover(false)}>
            <div>
            {hover 
            ? 
            <p style={{ position:"absolute", width:"256px", height:"256px", fontColor: "white" }}>Hello</p>
            : <h1 style={{ position:"absolute", maxHeight: "inherit", maxwidth: "inherit", backgroundColor:"#ffffe0" }}>cat</h1>
            }
            </div>
        </Card> 
    )
}

export default Card
