import React, { useState } from 'react'
import styled from 'styled-components'

const Card = ({color}) => {

    const [ hover, setHover ] = useState(false)

    const Card = styled.div`
        width: clamp(250px, 20vw, 250px);
        height: clamp(250px, 20vw, 250px);
        border: 1px solid silver;
        display: flex;
        flex-direction: column;
        overflow-Y: hidden; 
    `

    return (
        <Card onMouseOver={() => setHover(true)} onMouseLeave={() => setHover(false)} style={hover ? {backgroundColor:"#ebc8b2" , opacity: "30%"} : {backgroundColor: "#ebc8b2" }}>
            {hover 
            ? 
            <p style={{ position:"absolute", fontColor: "white" }}>Hello</p>
            : <h1 style={{ position:"absolute" }}>cat</h1>
            }
        </Card> 
    )
}

export default Card
