import React, { useRef } from 'react'
import './App.css';
import NameBox from './components/NameBox'
import LinkOverlay from './components/LinkOverlay'
import AppBar from './components/AppBar'
import styled, { keyframes } from 'styled-components'

const App = () => {

  const links = [
    {
      label: 'LinkedIn',
      url: 'https://www.linkedin.com/in/kyle-gloria/',
      image: 'https://www.iconninja.com/files/479/390/409/linkedin-icon.png',
      id: '1'
    },
    {
      label: 'GitHub',
      url: 'https://github.com/KGloria',
      image: 'https://www.iconninja.com/files/930/277/269/github-icon.png',
      id: '2'
    }
  ] 


  const pg1 = useRef(null)
  const pg2 = useRef(null)
  const pg3 = useRef(null)
  const pg4 = useRef(null)

  const Card = styled.div`
    width: 22vw;
    border: 6px solid white;
    display: flex;
    flex-direction: column;
    overflow-Y: hidden; 
  `

  return (
    <>

      <LinkOverlay links={links}/>
      <AppBar ref1={pg1} ref2={pg2} ref3={pg3}/>

      <div className="App">

        <div ref={pg1} className="App-header"> 
            <NameBox word="Kyle Gloria"/>       
              <p className='LandingPage-Desc'>Full Stack Developer | IT Professional</p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn More
          </a>
        </div>
        <div ref={pg2} className="App-header"> 
            <p className='LandingPage-Desc'>Projects</p>

            <Card>
              <div className="Projects-Card">
                <img alt="img" width="300px" src="https://news.artnet.com/app/news-upload/2019/01/Cat-Photog-Feat-256x256.jpg"></img>
                <h3 style={{ margin: '0' }}>Project1</h3>
                <p style={{ margin: '0' }}>description description description description description</p>
              </div>
            </Card>

        </div>

      <div ref={pg3} className="App-header" style={{ background: 'linear-gradient(180deg, #ebc8b2 0%, #ffffff 40%)' }}> 
        <p className='LandingPage-Desc'>Skills</p>
      </div>
      </div>
    </>
  );
}

export default App;
