import React, { useState } from 'react'
import styled from 'styled-components'

const Card = () => {

    const [ hover, setHover ] = useState(false)

    const handleHover = () => {
        setHover(!hover)
    }

    const Card = styled.div`
    width: 22vw;
    border: 6px solid white;
    display: flex;
    flex-direction: column;
    overflow-Y: hidden; 
  `

    return (
        <Card onHover={handleHover}>
            {hover 
            ? <p>Hello</p>
            : <img alt="img" width="300px" src="https://news.artnet.com/app/news-upload/2019/01/Cat-Photog-Feat-256x256.jpg"></img>
            }

        </Card> 
    )
}

export default Card
